def get_test_sets() -> Dict[str, List[Dict[str, Any]]]:
    """
    Get organized test sets for confidence evaluation.

    Returns:
        Dictionary with different test categories
    """
    # Separate examples by category for better organization
    obvious_examples = [ex for ex in EXAMPLES if ex.get("category", "").startswith("obvious_")]
    other_examples = [ex for ex in EXAMPLES if not ex.get("category", "").startswith("obvious_")]
    
    return {
        "standard": obvious_examples,
        "ambiguous": other_examples,
        "all": EXAMPLES
    }


def get_examples_by_category(category: str) -> List[Dict[str, Any]]:
    """
    Get examples filtered by category.

    Args:
        category: Category to filter by (e.g., 'strongly_positive', 'contradictory')

    Returns:
        List of examples matching the category
    """
    all_examples = EXAMPLES
    return [ex for ex in all_examples if ex.get("category") == category]



def print_dataset_summary():
    """
    Print a summary of the available datasets.
    """
    test_sets = get_test_sets()

    print("Dataset Summary:")
    print("=" * 50)

    for name, examples in test_sets.items():
        if name == "all":
            continue

        print(f"\n{name.upper()} ({len(examples)} examples):")

        # Count by expected sentiment
        sentiment_counts = {}
        category_counts = {}

        for ex in examples:
            sent = ex["expected"]
            cat = ex["category"]
            sentiment_counts[sent] = sentiment_counts.get(sent, 0) + 1
            category_counts[cat] = category_counts.get(cat, 0) + 1

        print(f"  Sentiment distribution: {sentiment_counts}")
        print(f"  Category distribution: {category_counts}")

        # Show a few examples
        print("  Sample examples:")
        for ex in examples[:3]:
            print(f"    '{ex['text']}' → {ex['expected']} ({ex['category']})")

    print(f"\nTotal examples: {len(test_sets['all'])}")

    print(f"\n🎯 Key Categories:")
    print(f"• STANDARD: Clear sentiment expressions (high confidence expected)")
    print(f"• AMBIGUOUS: Contradictory/unclear text (low confidence expected)")
    print(f"  - contradictory: Mixed positive/negative signals")
    print(f"  - ambiguous: Unclear or context-dependent")
    print(f"  - sarcastic: Requires context understanding")
    print(f"  - minimal: Very little information to work with")


if __name__ == "__main__":
    print_dataset_summary()